import React, { Component } from "react";
import moment from 'moment'; 
import "./filter.css";

class Filter extends Component {
  state = {
    currentDate : moment().format("YYYY-MM-DD"),
    category : "No category",
    length : "any",
    before : moment().format("YYYY-MM-DD"),
    after : "2000-01-01",
  };

  //sending category up to app.js
  handleChange = (event) => {
    this.setState({ [event.target.name]: [event.target.value] }, () =>
      this.props.updateCategory(this.state.category, this.state.length, this.state.before, this.state.after)
    );
  };

  render() {
    return (
      <form>
        <text id="filterTitle">
          {"Filters"}
          <br />
        </text>
        <label id="categories">Choose a category:</label>
        <select id="categoriesDropDown" name="category" onChange={this.handleChange}>
          <option value="No category">No category</option>
          <option value="Film & Animation">Film & Animation</option>
          <option value="Autos & Vehicles">Autos & Vehicles</option>
          <option value="Music">Music</option>
          <option value="Pets & Animals">Pets & Animals</option>
          <option value="Sports">Sports</option>
          <option value="Travel & Events">Travel & Events</option>
          <option value="Gaming">Gaming</option>
          <option value="People & Blogs">People & Blogs</option>
          <option value="Comedy">Comedy</option>
          <option value="News & Politics">News & Politics</option>
          <option value="Entertainment">Entertainment</option>
          <option value="Howto & Style">Howto & Style</option>
          <option value="Education">Education</option>
          <option value="Science & Technology">Science & Technology</option>
          <option value="Nonprofits & Activism">Nonprofits & Activism</option>
          <option value="Movies">Movies</option>
          <option value="Trailers">Trailers</option>
        </select>
        <label htmlFor="videoDuration">Video Length:</label>
        <select id="durationDropDown" name="length" onChange={this.handleChange}>
          <option value="any">Any length</option>
          <option value="short">0-4 Minutes</option>
          <option value="medium">4-20 Minutes</option>
          <option value="long">20+ Minutes</option>
        </select>
        <label for="publishedBefore">Published Before:</label>
        <input name="before" type="date" onkeydown="return false" min={this.state.after} max={this.state.currentDate} onChange={this.handleChange}>
        </input>
        <br />
        <label for="publishedAfter" >Published After:</label>
        <input name="after" type="date" onkeydown="return false"  min="2000-01-01" max={this.state.before} onChange={this.handleChange}>
        </input>
      </form>
    );
  }
}
export default Filter;
