import React, { Component } from "react";
import Title from "./components/title.js";
import MiddleRow from "./components/middleRow.js";
import RandomVideoButton from "./components/randomVideoButton.js";
import Filter from "./components/filter.js"

//todo: dont create the "words" array every time they click the button
class App extends Component {
  
  state = {
    videoURL: "",
    searchWords: [],
    category: 0,
  };

  async getVideo(keywords) {
    var filterCategory = true;
    var category = "44"; //GET THIS NUMBER FROM THE FILTER
    var keywords = await this.createWordList();
    var keyword = keywords[Math.floor(Math.random() * keywords.length + 1)];
    console.log("KEYWORD AT 66:" + keywords[66]);
    console.log("Chosen Keyword: " + keyword);
    const youtubeAPIKey = "AIzaSyAWwZk3tMo_OZp0JZ9Y17oxykJpng6Q-nY";

    var baseApiUrl = 
    "https://www.googleapis.com/youtube/v3/search?part=snippet&maxResults=25&q=" +
    keyword 

    if (filterCategory == true){
      baseApiUrl += "&type=video" +"&videoCategoryId=" + category 
    }
    //can add more filters here
    //USE A MAP TO ASSIGN CATEGORIES TO NUMBERS
    //get categories from user
    //published after/before
    baseApiUrl += "&key=" + youtubeAPIKey;


    const APIurl =
      "https://www.googleapis.com/youtube/v3/search?part=snippet&maxResults=25&q=" +
      keyword +
      "&type=video" +
      "&videoCategoryId=" +
      category +
      "&key=" +
      youtubeAPIKey;
    //let res = await fetch(APIurl);
    let res = await fetch(baseApiUrl);
    let data = await res.json(); 
    var searchLength = data.items.length;
    var idIndex = Math.floor(Math.random() * searchLength);
    console.log("LENGTH: " + data.items.length);
    var randomURL =
      "https://www.youtube.com/embed/" + data.items[idIndex].id.videoId;
    console.log("Our URL: " + randomURL);
    console.log(data);
    this.setState({ videoURL: randomURL });
    return randomURL;
  }

  async createWordList() {
    var words = [];
    let res = await fetch("/common-words.json");
    let data = await res.json();
    console.log(data);
    for (var i = 0; i < data.length; i++) {
      words.push(data[i]);
    }
    return words;
  }

  getRandomVideo = () => {
    this.getVideo();
  };

  render() {
    return (
      <body>
        <div>
          <Title />
          <RandomVideoButton onClick={this.getRandomVideo} />
          <MiddleRow url={this.state.videoURL} changeFunction={this.handleChange} />
        </div>
      </body>
    );
  }
}
export default App;
