import React, { Component } from "react";
import "./filter.css";

var catagoriesMap = new Map();
catagoriesMap.set("No category", 0);
catagoriesMap.set("Film & Animation", 1);
catagoriesMap.set("Autos & Vehicles", 2);
catagoriesMap.set("Music", 10);
catagoriesMap.set("Pets & Animals", 15);
catagoriesMap.set("Sports", 17);
catagoriesMap.set("Travel & Events", 19);
catagoriesMap.set("Gaming", 20);   
catagoriesMap.set("People & Blogs", 22);
catagoriesMap.set("Comedy", 23);
catagoriesMap.set("Entertainment", 24);
catagoriesMap.set("News & Politics", 25);
catagoriesMap.set("Howto & Style", 26);
catagoriesMap.set("Education", 27);
catagoriesMap.set("Science & Technology", 28);
catagoriesMap.set("Nonprofits & Activism", 29);
catagoriesMap.set("Movies", 30);
catagoriesMap.set("Trailers", 44);

class Filter extends Component {
  state = {
    categoryNumber: 0,
  };

  handleChange = (event) => {
    console.log(event.target.value);
    console.log(catagoriesMap.get(event.target.value));
    this.setState({ categoryNumber: catagoriesMap.get(event.target.value) });
    console.log("state: ", state.categoryNumber);
    this.state
  };

  render() {
    return (
      <form>
        <text id="filterTitle">
          {"Filters"}
          <br />
        </text>
        <label id="categories">Choose a category:</label>
        <select id="categoriesDropDown" name="categories" onChange={this.handleChange}>
          <option value="No category">No category</option>
          <option value="Film & Animation">Film & Animation</option>
          <option value="Autos & Vehicles">Autos & Vehicles</option>
          <option value="Music">Music</option>
          <option value="Pets & Animals">Pets & Animals</option>
          <option value="Sports">Sports</option>
          <option value="Travel & Events">Travel & Events</option>
          <option value="Gaming">Gaming</option>
          <option value="People & Blogs">People & Blogs</option>
          <option value="Comedy">Comedy</option>
          <option value="News & Politics">News & Politics</option>
          <option value="Entertainment">Entertainment</option>
          <option value="Howto & Style">Howto & Style</option>
          <option value="Education">Education</option>
          <option value="Science & Technology">Science & Technology</option>
          <option value="Nonprofits & Activism">Nonprofits & Activism</option>
          <option value="Movies">Movies</option>
          <option value="Trailers">Trailers</option>
        </select>
        <label htmlFor="keyWords">Key Words:</label>
        <input
          autoComplete="off"
          type="text"
          id="keyWordsInput"
          name="keywords"
          placeholder="Insert"
        ></input>
        <label for="maxViews">Maximum Views:</label>
        <input
          autocomplete="off"
          type="text"
          id="maxViewsInput"
          name="maxviews"
          placeholder="Insert"
        ></input>
        <br />
        <label for="minViews">Minimum Views:</label>
        <input
          autocomplete="off"
          type="text"
          id="minViewsInput"
          name="minviews"
          placeholder="Insert"
        ></input>
      </form>
    );
  }
}
export default Filter;
